import { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import headerImg from "../assets/img/bio-sensor.png";
import { ArrowRightCircle } from 'react-bootstrap-icons';
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Banner = () => {
  const toRotate = ["Continuous monitoring", "Creatinine sensing", "AKI"];
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const period = 2000;

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsDeleting(!isDeleting);
      setCurrentIndex((prevIndex) => (prevIndex + 1) % toRotate.length);
    }, period);

    return () => clearTimeout(timer);
  }, [isDeleting, currentIndex, period]);

  const currentText = toRotate[currentIndex];

  return (
    <section className="banner" id="home">
      <Container>
        <Row className="aligh-items-center">
          <Col xs={12} md={6} xl={7}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <span className="tagline">Welcome to my Portfolio</span>
                  <h1>
                    {`AixSense`}
                    <span className="txt-rotate">
                      <span className={`text ${isDeleting ? 'deleting' : ''}`}>{currentText}</span>
                      <span className={`text ${isDeleting ? '' : 'deleting'}`}>{toRotate[(currentIndex + 1) % toRotate.length]}</span>
                    </span>
                  </h1>
                  <p>AixSense is a startup from RWTH Aachen building an innovative biosensor for continuously monitoring kidney disease</p>
                  <button onClick={() => console.log('connect')}>Find out more<ArrowRightCircle size={25} /></button>
                </div>
              )}
            </TrackVisibility>
          </Col>
          <Col xs={12} md={12} xl={5}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__zoomIn" : ""}>
                  <img src={headerImg} alt="Header Img" style={{ width: "100%", maxWidth: "250px" }} />
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <style jsx>{`
        .txt-rotate {
          display: inline-block;
          overflow: hidden;
        }

        .text {
          display: block;
          animation: rotate 1s linear infinite;
        }

        .deleting {
          animation-direction: reverse;
        }

        @keyframes rotate {
          from {
            transform: translateY(0);
          }
          to {
            transform: translateY(-100%);
          }
        }
      `}</style>
    </section>
  );
};
